<?php

/**
 * Acción para editar una servicio.
 *
 * @author Marcos
 * @since 21-05-2012
 *
 */
abstract class EditarServicioAction extends EditarAction {

	protected function getEntidad() {		$oServicio = new Servicio();		if (isset($_POST ['cd_servicio'])){			$oServicio->setCd_servicio($_POST['cd_servicio']);		}		if (isset($_POST ['cd_usuario'])){			$oServicio->setCd_usuario($_POST['cd_usuario']);		}		if (isset($_POST ['cd_cliente'])){			$oServicio->setCd_cliente($_POST['cd_cliente']);		}		if (isset($_POST ['nu_monto'])){			$oServicio->setNu_monto($_POST['nu_monto']);		}		if (isset($_POST ['ds_kmshoras'])){			$oServicio->setDs_kmshoras($_POST['ds_kmshoras']);		}		if (isset($_POST ['cd_sucursal'])){			$oServicio->setCd_sucursal($_POST['cd_sucursal']);		}		if (isset($_POST ['dt_ingresovehiculo'])){			$dt_ingresovehiculo = str_replace("/","-", $_POST['dt_ingresovehiculo']);			$dt_fechahora = explode( " ", $dt_ingresovehiculo );			$dt_fecha = implode("-", array_reverse(explode("-", $dt_fechahora[0])));			$dt_hora = $dt_fechahora[1];			$dt_ingresovehiculo = $dt_fecha.' '.$dt_hora;			$oServicio->setDt_ingresovehiculo($dt_ingresovehiculo);		}				if(isset($_POST ['cd_tiposervicio'])){			$oServicio->setCd_tiposervicio(addslashes($_POST ['cd_tiposervicio']));		}				if (isset($_POST ['dt_compromisoentrega'])){			$dt_compromisoentrega = str_replace("/","-", $_POST['dt_compromisoentrega']);			$dt_compromisoentrega = implode("-", array_reverse(explode("-", $dt_compromisoentrega)));			$oServicio->setDt_compromisoentrega($dt_compromisoentrega);		}				if (isset($_POST ['ds_descpedidocte'])){			$oServicio->setDs_descpedidocte($_POST['ds_descpedidocte']);		}				if (isset($_POST ['ds_obsgral'])){			$oServicio->setDs_obsgral($_POST['ds_obsgral']);		}				if (isset($_POST ['ds_diagyreprealizada'])){			$oServicio->setDs_diagyreprealizada($_POST['ds_diagyreprealizada']);		}				if (isset($_POST ['ds_repuestosusados'])){			$oServicio->setDs_repuestosusados($_POST['ds_repuestosusados']);		}				if (isset($_POST ['ds_mecanicos'])){			$oServicio->setDs_mecanicos($_POST['ds_mecanicos']);		}				if (isset($_POST ['ds_instmedusados'])){			$oServicio->setDs_instmedusados($_POST['ds_instmedusados']);		}				if (isset($_POST ['ds_tiempomanoobra'])){			$oServicio->setDs_tiempomanoobra($_POST['ds_tiempomanoobra']);		}				if ($_POST ['bl_pagado']){			$oServicio->setBl_pagado(1);		}		$oServicio->setCd_usuario($_SESSION['cd_usuarioSession']);		$oVehiculoservicio = new Vehiculoservicio();				if (isset($_POST ['cd_vehiculoservicio'])){			$oVehiculoservicio->setCd_vehiculoservicio($_POST['cd_vehiculoservicio']);		}				if (isset($_POST ['nu_motor'])){			$oVehiculoservicio->setNu_motor($_POST['nu_motor']);		}				if (isset($_POST ['nu_anio'])){			$oVehiculoservicio->setNu_anio($_POST['nu_anio']);		}			if (isset($_POST ['nu_chasis'])){			$oVehiculoservicio->setNu_chasis($_POST['nu_chasis']);		}				if (isset($_POST ['dt_venta'])){			$dt_venta = str_replace("/","-", $_POST['dt_venta']);			$dt_venta = implode("-", array_reverse(explode("-", $dt_venta)));			$oVehiculoservicio->setDt_venta($dt_venta);		}				if (isset($_POST ['ds_modelo'])){			$oVehiculoservicio->setDs_modelo($_POST['ds_modelo']);		}						return array("servicio" => $oServicio, "vehiculoservicio" => $oVehiculoservicio );				//return $oServicio;
	}

}