<?php

/** * * @author Lucrecia * @since 31-01-2011 * * Manager para modelos. * */class ModeloManager extends ReferenciaManager{	protected function getReferenciaQuery(){		return new ModeloQuery();	}	public function agregarModelo(Modelo $oModelo){		//persistir modelo en la bbdd.		ModeloQuery::insertModelo( $oModelo );	}	/**	 * se modifica una modelo.	 * @param Modelo $oModelo a modificar.	 */	public function modificarModelo(Modelo $oModelo){		//persistir los cambios de la modelo en la bbdd.		ModeloQuery::modificarModelo($oModelo);	}	/**	 * eliminar una modelo.	 * @param $cd_modelo identificador de la modelo a eliminar	 */	public function eliminarModelo($cd_modelo){		$oModelo = new Modelo();		$oModelo->setCd_modelo ( $cd_modelo );		ModeloQuery::eliminarModelo($oModelo);	}	/**	 * se listan modeloes.	 * @param $criterio	 * @return unknown_type	 */	public function getModelos(CriterioBusqueda $criterio){		$modelos = ModeloQuery::getmodelos($criterio);		return $modelos;	}	public function getModelosDeMarca($cd_marca){		$criterio = new CriterioBusqueda();		$criterio->addFiltro('M.cd_marca', $cd_marca, '=');                 $criterio->addOrden('ds_modelo');		$modelos = ModeloQuery::getmodelos($criterio);		return $modelos;	}	/**	 * obtiene una modelo espec�fico dado un identificador.	 * @param $cd_modelo identificador de la modelo a recuperar	 * @return unknown_type	 */	public function getModeloPorId($cd_modelo){		$criterio = new CriterioBusqueda();		$criterio->addFiltro('M.cd_modelo', $cd_modelo, '=');		$oModelo =  ModeloQuery::getModelo ( $criterio );		return $oModelo;	}	/**	 * obtiene la cantidad de modelos dado un filtro.	 * @param $filtro filtro de b�squeda.	 * @return cantidad de modelos	 */	public function getCantidadModelos( CriterioBusqueda $criterio){		$cant =  ModeloQuery::getCantModelos( $criterio );		return $cant;	}	//INTERFACE IListar.	function getEntidades ( CriterioBusqueda $criterio ){		return  $this->getModelos( $criterio );	}	function getCantidadEntidades ( CriterioBusqueda $criterio){		return $this->getCantidadModelos( $criterio );	}}