<?php
/** * * @author marcos * @since 21-05-2012 * * Manager para vehiculoservicio. * */class VehiculoservicioManager extends ReferenciaManager{	protected function getReferenciaQuery(){		return new VehiculoservicioQuery();	}	public function agregarVehiculoservicio(Vehiculoservicio $oVehiculoservicio){		//persistir vehiculoservicio en la bbdd.		VehiculoservicioQuery::insertVehiculoservicio( $oVehiculoservicio );	}	/**	 * se modifica una vehiculoservicio.	 * @param Vehiculoservicio $oVehiculoservicio a modificar.	 */	public function modificarVehiculoservicio(Vehiculoservicio $oVehiculoservicio){		//persistir los cambios de la vehiculoservicio en la bbdd.		VehiculoservicioQuery::modificarVehiculoservicio($oVehiculoservicio);	}	/**	 * eliminar una vehiculoservicio.	 * @param $cd_vehiculoservicio identificador de la vehiculoservicio a eliminar	 */	public function eliminarVehiculoservicio($cd_vehiculoservicio){		$oVehiculoservicio = new Vehiculoservicio();		$oVehiculoservicio->setCd_vehiculoservicio ( $cd_vehiculoservicio );		VehiculoservicioQuery::eliminarVehiculoservicio($oVehiculoservicio);	}	/**	 * se listan vehiculoservicioes.	 * @param $criterio	 * @return unknown_type	 */	public function getVehiculosservicios(CriterioBusqueda $criterio){		$vehiculosservicios = VehiculoservicioQuery::getvehiculosservicios($criterio);		return $vehiculosservicios;	}		/**	 * obtiene una vehiculoservicio espec�fico dado un identificador.	 * @param $cd_vehiculoservicio identificador de la vehiculoservicio a recuperar	 * @return unknown_type	 */	public function getVehiculoservicio($criterio){		$oVehiculoservicio =  VehiculoservicioQuery::getVehiculoservicio ( $criterio );		return $oVehiculoservicio;	}	/**	 * obtiene la cantidad de vehiculosservicios dado un filtro.	 * @param $filtro filtro de b�squeda.	 * @return cantidad de vehiculosservicios	 */	public function getCantidadVehiculosservicios( CriterioBusqueda $criterio){		$cant =  VehiculoservicioQuery::getCantVehiculosservicios( $criterio );		return $cant;	}	//INTERFACE IListar.	function getEntidades ( CriterioBusqueda $criterio ){		return  $this->getVehiculosservicios( $criterio );	}	function getCantidadEntidades ( CriterioBusqueda $criterio){		return $this->getCantidadVehiculosservicios( $criterio );	}}
